/** @type {ReactContext<string | number>} */
const ShuffleSeedContext = createContext<string | number>('');

// Define an interface for shuffle props
/** @typedef {Object} ShuffleProps
 *  @property {string | number} [seed] - The seed for randomization
 *  @property {boolean} [isDisabled] - Whether the shuffle is disabled
 *  @property {number} [limit] - The limit of children to display
 *  @property {ReactNode} children - The children to shuffle
