/**
 * A class that represents an image with a size and an array of pixels.
 */
class Image {
  /**
   * The size of the image as a vector.
   */
  public readonly size: Vector;

  /**
   * The array of pixels as a two-dimensional array of colors.
   */
  private pixels: Color[][];

  /**
   * Creates a new image with the given size and fills it with the default color.
   * @param size The size of the image as an object with x and y properties.
   * @param defaultColor The default color to fill the image with. Defaults to transparent black.
   */
  constructor(size: IVector, defaultColor: Color = Color.fromHex('#00000000')) {
    this.size = Vector.fromObject(size);
    this.pixels = [];
    for (let i = 0; i < this.size.y; i++) {
      this.pixels[i] = [];
      for (let j = 0; j < this.size.x; j++) {
        this.pixels[i][j] = defaultColor;
      }
    }
  }

  /**
   * Gets the color of the pixel at the given position.
   * @param position The position of the pixel as an object with x and y properties.
   * @returns The color of the pixel as a Color object.
   * @throws Error if the position is invalid or out of bounds.
   */
  public getPixel(position: IVector): Color {
    if (
      (position.x || 0) >= 0 &&
      (position.x || 0) < this.size.x &&
      (position.y || 0) >= 0 &&
      (position.y || 0) < this.size.y
    ) {
      return this.pixels[position.y || 0][position.x || 0];
    } else {
      throw new Error(
        `Invalid pixel position (${position.x || 0}, ${position.y || 0})`
      );
    }
  }

  /**
   * Sets the color of the pixel at the given position.
   * @param position The position of the pixel as an object with x and y properties.
   * @param color The color to set the pixel to as a Color object.
   * @throws Error if the position is invalid or out of bounds.
   */
  public setPixel(position: IVector, color: Color): void {
    if (
      (position.x || 0) >= 0 &&
      (position.x || 0) < this.size.x &&
      (position.y || 0) >= 0 &&
      (position.y || 0) < this.size.y
    ) {
      this.pixels[position.y || 0][position.x || 0] = color;
    } else {
      throw new Error(
        `Invalid pixel position (${position.x || 0}, ${position.y || 0})`
      );
    }
  }
}

